import { time, loadFixture } from "@nomicfoundation/hardhat-network-helpers";
import { anyValue } from "@nomicfoundation/hardhat-chai-matchers/withArgs";
import { expect, assert } from "chai";
import { ethers, network } from "hardhat";
import { deploy } from "./utils/helpers";
import { any } from "hardhat/internal/core/params/argumentTypes";

describe("Tunnels", function () {
  let owner: any,
    fxRoot: any,
    fxChild: any,
    childTunnel: any,
    fxerc20: any,
    checkpointManager: any,
    rootTunnel: any;

  beforeEach(async () => {
    const accounts = await ethers.getSigners();
    owner = accounts[0];
    fxChild = "0xCf73231F28B7331BBe3124B907840A94851f9f11";
    fxRoot = "0x3d1d3E34f7fB6D26245E6640E1c50710eFFf15bA";
    checkpointManager = "0x2890bA17EfE978480615e330ecB65333b880928e";
    fxerc20 = await deploy("FxERC20");
    rootTunnel = await deploy("FxERC20RootTunnel", [
      checkpointManager,
      owner.address,
      fxerc20.address,
    ]);

    childTunnel = await deploy("FxERC20ChildTunnel", [
      owner.address,
      fxerc20.address,
    ]);
    const tx = await childTunnel.setFxRootTunnel(rootTunnel.address);
    console.log("tx", tx);
  });

  describe("process message from root", function () {
    it("sets token mapping", async () => {
      expect(
        await childTunnel.processMessageFromRoot(
          1,
          rootTunnel.address,
          ""
        )
      ).to.emit(childTunnel, "TokenMapped");
      await childTunnel.processMessageFromRoot(
        1,
        rootTunnel.address,
        ""
      );
    });
  });
});
